import unittest

from captcha import *


class TestCaptcha(unittest.TestCase):
    test_captchas = {
        "KBDARB": "data:image/jpeg;base64,",
        "EWEUPN": "data:image/jpeg;base64,",
    }

    def test_captcha_url(self):
        self.assertEqual(
            decode_captcha_url("https://raw.githubusercontent.com/neati/captcha/main/captcha.jpg"),
            "NACDRA",
        )

    def test_captcha_base64(self):
        for k, v in self.test_captchas.items():
            self.assertEqual(decode_captcha_base64(v), k)
